// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/*
 *  Minimal spot market that supports:
 *    • limitOrder(): maker deposits BASE, setting a human‑price * 1e9 (priceInternal)
 *    • buyOrder(): taker spends QUOTE to buy from stored orders, executing the exact math
 *      described in the python snippet (protocol fee 0.03 %, maker rebate 0.005 %).
 *
 *  Tokens:
 *    – QUOTE: 6‑decimals (e.g. USDC style)
 *    – BASE : 18‑decimals (e.g. WETH style)
 *
 *  The contract is *not* production‑ready (no order cancellation, no re‑entrancy guard on
 *  withdrawals, no gas optimisations, no access‑control on feeRecipient), but it’s enough
 *  to prove the maths.
 */

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract MinimalSpotMarket {
    using SafeERC20 for IERC20;

    // === CONSTANTS (100 % = 1 000 000) =======================================
    uint256 internal constant FEE_DEN             = 1e6;
    uint256 internal constant PROTOCOL_FEE_NUM    = 300;   // 0.03 %
    uint256 internal constant MAKER_REBATE_NUM    = 50;    // 0.005 %
    uint256 internal constant PRICE_FACTOR        = 1e9;   // 1 “human” price unit = 1e9 internal
    uint256 internal constant SCALE_FACTOR        = 1e21;  // quote‑to‑internal multiplier
    uint256 internal constant QUOTE_DECIMALS      = 1e6;   // 10**6
    uint256 internal constant BASE_DECIMALS       = 1e18;  // 10**18

    uint256 internal constant FEE_MULT_NUM =
        (FEE_DEN - PROTOCOL_FEE_NUM) * (FEE_DEN - MAKER_REBATE_NUM);                // (1‑0.03 %)(1‑0.005 %) numerator
    uint256 internal constant FEE_MULT_DEN = FEE_DEN * FEE_DEN;                     // 1e12

    // === STORAGE =============================================================
    struct Order {
        uint256 internalSize;   // baseExternal * priceInternal
        uint256 priceInternal;  // humanPrice * 1e9
        address maker;
    }

    IERC20  public immutable quote;      // 6‑decimals token (e.g. USDC)
    IERC20  public immutable base;       // 18‑decimals token (e.g. WETH)
    address public immutable feeRecipient;

    Order[] public orderBook;            // naive FIFO

    // === CONSTRUCTOR =========================================================
    constructor(IERC20 _quote, IERC20 _base, address _feeRecipient) {
        quote         = _quote;
        base          = _base;
        feeRecipient  = _feeRecipient;
    }

    // === MAKER SIDE ==========================================================
    /// @notice maker deposits `baseAmountExternal` (token units, 18‑decimals)
    ///         and lists it for sale at `priceInternal` (humanPrice * 1e9)
    function limitOrder(uint256 baseAmountExternal, uint256 priceInternal) external {
        require(priceInternal > 0, "price 0");

        // pull BASE from maker
        base.safeTransferFrom(msg.sender, address(this), baseAmountExternal);

        // compute internal size: externalBase * priceInternal
        uint256 internalSize = baseAmountExternal * priceInternal;

        orderBook.push(Order({
            internalSize:  internalSize,
            priceInternal: priceInternal,
            maker:         msg.sender
        }));
    }

    // === TAKER SIDE ==========================================================
    /// @notice taker spends up to `quoteAmountExternal` QUOTE (6‑decimals) to buy BASE
    function buyOrder(uint256 quoteAmountExternal) external {
        // pull QUOTE from taker
        quote.safeTransferFrom(msg.sender, address(this), quoteAmountExternal);

        // convert to internal units
        uint256 grossInternalQuote = quoteAmountExternal * SCALE_FACTOR; // already contains 10**6
        uint256 protocolFeeInternal = (grossInternalQuote * PROTOCOL_FEE_NUM) / FEE_DEN;
        uint256 remainingInternal   = grossInternalQuote - protocolFeeInternal;

        uint256 quoteSpentInternal  = 0;

        // iterate through orders
        uint256 i = 0;
        while (i < orderBook.length && remainingInternal > 0) {
            Order storage o = orderBook[i];

            uint256 needQuoteScaled = (o.internalSize * FEE_MULT_DEN) / FEE_MULT_NUM;

            if (needQuoteScaled > remainingInternal) {
                // -------- PARTIAL FILL ---------------------------------------
                uint256 internalQuoteUsed = remainingInternal;

                uint256 makerRebateInternal = (internalQuoteUsed * MAKER_REBATE_NUM) / FEE_DEN;
                uint256 fillableBaseInternal =
                    (internalQuoteUsed * (FEE_DEN - MAKER_REBATE_NUM)) / FEE_DEN;

                // update order size
                o.internalSize -= fillableBaseInternal;

                remainingInternal = 0;
                quoteSpentInternal += internalQuoteUsed;

                // transfers ---------------------------------------------------
                _payout(o.maker, internalQuoteUsed, fillableBaseInternal, o.priceInternal);
                break;
            } else {
                // -------- FULL FILL ------------------------------------------
                uint256 internalQuoteUsed = needQuoteScaled;
                uint256 makerRebateInternal = (o.internalSize * MAKER_REBATE_NUM) / FEE_DEN;

                // zero‑out order (we’ll swap with last and pop to save gas)
                uint256 last = orderBook.length - 1;
                if (i != last) orderBook[i] = orderBook[last];
                orderBook.pop();

                remainingInternal   -= internalQuoteUsed;
                quoteSpentInternal  += internalQuoteUsed;

                _payout(o.maker, internalQuoteUsed, o.internalSize, o.priceInternal);
                continue; // don’t increment i; orderBook[i] is a new entry after swap
            }
        }

        // === FEES AND REFUND =================================================
        uint256 protocolFeeExternal = protocolFeeInternal / SCALE_FACTOR;
        if (protocolFeeExternal > 0) {
            quote.safeTransfer(feeRecipient, protocolFeeExternal);
        }

        uint256 quoteSpentExternal  = quoteSpentInternal / SCALE_FACTOR;
        uint256 refundExternal      = quoteAmountExternal - protocolFeeExternal - quoteSpentExternal;

        if (refundExternal > 0) quote.safeTransfer(msg.sender, refundExternal);
    }

    // === INTERNAL HELPERS ====================================================
    function _payout(
        address maker,
        uint256 makerQuoteInternal,     // full internal quote that goes to maker (includes rebate)
        uint256 takerBaseInternal,      // internal base sent to taker
        uint256 priceInternal           // order price
    ) internal {
        uint256 quoteExternal = makerQuoteInternal / SCALE_FACTOR;
        uint256 baseExternal  = takerBaseInternal  / priceInternal; // since internal = external * priceInternal

        // transfers
        quote.safeTransfer(maker, quoteExternal);
        base.safeTransfer(msg.sender, baseExternal);
    }

    // === VIEW FUNCTIONS (for tests) =========================================
    function orderCount() external view returns (uint256) { return orderBook.length; }
    function getOrder(uint256 idx) external view returns (Order memory) { return orderBook[idx]; }
}

